    <script>
        function myFunction3() {
            var div = document.getElementById("menu");
            var but = document.getElementById("botonmostrar");
            var barras = document.getElementById("icon1");
            var close = document.getElementById("icon2");
            if (div.classList.contains("hidden")) {
                div.classList.remove("hidden");
                localStorage.setItem("menu", true);
                barras.style.display = "none";
                close.style.display = "block";
                document.addEventListener(
                    "click",
                    function(e) {
                        var clic = e.target;
                        if (
                            localStorage.getItem("menu") &&
                            clic !== div &&
                            clic !== barras &&
                            clic !== but
                        ) {
                            div.classList.add("hidden");
                            barras.style.display = "block";
                            close.style.display = "none";
                        } else {}
                    },
                    false
                );
            } else {
                localStorage.setItem("menu", false);
                div.classList.add("hidden");
                barras.style.display = "block";
                close.style.display = "none";
            }
        }

        function irAbajo() {
            window.addEventListener("scroll", () => {
                var scroll = document.documentElement.scrollTop;
                var botonabajo = document.getElementById("botonabajo");
                if (botonabajo == null) {
                    return;
                }
                if (scroll < 80) {
                    botonabajo.style.left = 80 + "%";
                } else {
                    botonabajo.style.left = -85 + "%";
                }
            });
        }

        irAbajo();
    </script>

